SELECT RO.ROLE_NO, RO.ROLE_DESC, UR.USER_ID, US.USER_NAME FROM KPDBA.DYMCHK_ROLES RO JOIN ( SELECT ROLE_NO, USER_ID FROM KPDBA.DYMCHK_ROLES_USERS UNION SELECT ROLE_NO, USER_ID FROM KPDBA.DYMCHK_USERS_ROLES
) UR ON RO.ROLE_NO = UR.ROLE_NO JOIN KPDBA.USERS US ON UR.USER_ID = US.USER_ID WHERE US.DEL_STAT = 'N' AND RO.ROLE_NO = :S_ROLE_NO